package com.tnsif.basic;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=15;
		int c=11;
   for(int i=c;i<=a;i++)
   {
	   for(int j=1;j<=10;j++) {
	  System.out.println(i+"*"+j+"="+i*j);
  }
	   System.out.println("***********************");
	}

}
}